-- Fonction pour accent insensitive
CREATE OR REPLACE FUNCTION unaccent_string(text) RETURNS text AS $$
DECLARE
    input_string text := $1;
BEGIN

input_string := translate(input_string, 'âãäåāăąÁÂÃÄÅĀĂĄ', 'aaaaaaaaaaaaaaa');
input_string := translate(input_string, 'èééêëēĕėęěĒĔĖĘĚ', 'eeeeeeeeeeeeeee');
input_string := translate(input_string, 'ìíîïìĩīĭÌÍÎÏÌĨĪĬ', 'iiiiiiiiiiiiiiii');
input_string := translate(input_string, 'óôõöōŏőÒÓÔÕÖŌŎŐ', 'ooooooooooooooo');
input_string := translate(input_string, 'ùúûüũūŭůÙÚÛÜŨŪŬŮ', 'uuuuuuuuuuuuuuuu');

return input_string;
END;
$$ LANGUAGE plpgsql;

--Scripts de créations
CREATE TABLE Account
(
    id serial PRIMARY KEY,
    login text UNIQUE NOT NULL,
    password text NOT NULL,
    firstname text,
    lastname text,
    email text UNIQUE NOT NULL,
    birthdate date,
    image varchar default('data:image/jpeg;base64,')
);

CREATE TABLE Difficulty
(
    id serial PRIMARY KEY,
    name text UNIQUE NOT NULL
);

CREATE TABLE Grade
(
    id serial PRIMARY KEY,
    name text UNIQUE NOT NULL
);

CREATE TABLE Questionnaire
(
    id serial PRIMARY KEY,
    color text NOT NULL,
    name text NOT NULL,
    pointsmax decimal NOT NULL,
    showpoints boolean default(false),
    lastmodified timestamp NOT NULL default(current_timestamp),
    grade int REFERENCES grade(id),
    category text NOT NULL,
    theme text NOT NULL,
    account int REFERENCES account(id)
);

CREATE TABLE Question
(
    id serial PRIMARY KEY,
    color text NOT NULL,
    lastmodified timestamp NOT NULL default(current_timestamp),
    grade int REFERENCES grade(id),
    category text NOT NULL,
    theme text NOT NULL,
    account int REFERENCES account(id),
    difficulty int REFERENCES difficulty(id),
    question text NOT NULL,
    response text NOT NULL
);

CREATE TABLE UsingQuestion
(
    idquestionnaire int REFERENCES questionnaire(id),
    idquestion int REFERENCES question(id),
    PRIMARY KEY(idquestionnaire,idquestion),
    points decimal default(0) CONSTRAINT points CHECK (points >= 0)
);

CREATE TABLE Commentary
(
    id serial PRIMARY KEY,
    account int REFERENCES account(id),
    body text NOT NULL,
    posted timestamp NOT NULL default(current_timestamp),
    idquestion int REFERENCES question(id),
    seen boolean default(false)
);

CREATE TABLE Rate
(
	idquestion int REFERENCES question(id),
    idaccount int REFERENCES account(id),
    PRIMARY KEY (idquestion,idaccount),
    rate decimal NOT NULL CONSTRAINT rate_note CHECK (rate>=0 AND rate<=5)
);

