--Informations de bases d'une question
SELECT
	question.id,
    color,
    grade.name AS level,
    category,
    theme,
    rate AS note,
    question,
    response,
    (
        SELECT
            count(*)
        FROM
            rate
        WHERE
            idquestion = question.id
    ) AS avis,
    (
        SELECT
            count(*)
        FROM
            commentary
        WHERE
            idquestion = question.id
    ) AS comments,
    uses,
    account.login
FROM
	question
	LEFT JOIN grade ON grade.id = question.grade
    LEFT JOIN account ON account.id = question.account
