-- Informations de bases d'un questionnaire
SELECT
		account.login,
		questionnaire.id,
    color,
    questionnaire.name AS QuestionnaireName,
    grade.name AS Grade,
    category,
    theme,
    (
    	SELECT
        	difficulty.name
        FROM
        	difficulty
        WHERE
        	difficulty.id = (
            					SELECT
                					question.difficulty
                				FROM
                					usingquestion
                					LEFT JOIN question ON question.id = usingquestion.idquestion
                				WHERE
                					idquestionnaire = questionnaire.id
                				ORDER BY
                					question.difficulty ASC
                				LIMIT 1
                            )
    ) AS difficultymin,
    (
    	SELECT
        	difficulty.name
        FROM
        	difficulty
        WHERE
        	difficulty.id = (
            					SELECT
                					question.difficulty
                				FROM
                					usingquestion
                					LEFT JOIN question ON question.id = usingquestion.idquestion
                				WHERE
                					idquestionnaire = questionnaire.id
                				ORDER BY
                					question.difficulty DESC
                				LIMIT 1
                            )
    ) AS difficultymax,
    (
    	SELECT
        	difficulty.name
        FROM
        	difficulty
        WHERE
        	difficulty.id = (
            					SELECT
                					ROUND(AVG(question.difficulty))
                				FROM
                					usingquestion
                					LEFT JOIN question ON question.id = usingquestion.idquestion
                				WHERE
                					idquestionnaire = questionnaire.id
                            )
    ) AS difficultymoy,
    pointsmax,
    showpoints,
    lastmodified,
    (
        SELECT
        	count(*)
        FROM
        	usingquestion
        WHERE
        	idquestionnaire = questionnaire.id
    ) AS numberofquestions
FROM
    questionnaire
    LEFT JOIN grade ON grade.id = questionnaire.grade
    LEFT JOIN account ON account.id = questionnaire.account

